import React, { useState, useEffect} from 'react';
import { node } from 'prop-types';
import fetchApi from '../_services/fetchApi';
import MachineContext from './MachineContext';

function Provider({ children }) {
  const [assets, setAssets] = useState([]);
  const [status, setStatus] = useState([]);
  const [count, setCount] = useState([]);

  useEffect(() => {
    async function fetchAssets() {
      const result = await fetchApi();
      const data = await result.data;
      const counting = data.reduce((acc, asset) => {
        if (!acc[asset.status] && asset.status) {
          acc[asset.status] = 1;
        } else {
          acc[asset.status] = acc[asset.status] + 1;
        }
        return acc;
      }, {});

      const status = Object.keys(counting);
      const count = Object.value(counting);

      setAssets(data);
      setStatus(status);
      setCount(count)
    }
    fetchAssets();
  }, []);

  const contextValue = {
    assets,
    status,
    count,
  };

  return (
    <MachineContext.Provider value={ contextValue }>
      {children}
    </MachineContext.Provider>
  );
}

Provider.propTypes = {
  children: node,
}.isRequired;

export default Provider;
