import React, { useContext } from 'react';
import { useParams } from "react-router-dom";
import MachineContext from '../../_context/MachineContext';
import { trim } from '../../_utils/utils';

const arr = [];

function Machine() {
  const { assets = arr} = useContext(MachineContext);
  const { machine } = useParams();

  const asset = assets.find((item) => {
    return trim(item.name) === machine;
  });

  return ( 
    <div className="flex-1 sm:px-12 ">
      { asset && (
        <>
          <div className="flex my-2 space-y-1">
            <h2 className="text-xl font-semibold sm:text-2xl">{asset.name}</h2>
            <p className="text-xl px-5  dark:text-coolGray-400">{asset.sensors}</p>
            <span class="text-xl text-green-500 mr-8">Saude: {Math.floor(Number(asset.healthscore))}%</span>
          </div>    
          <img src={asset.image} alt="Profile face" class="w-100 h-100 mx-auto rounded" />
        </>
        )
      }
    </div>
  );
}

export default Machine;
