import React, { useContext } from 'react';
import { useParams } from "react-router-dom";
import MachineContext from '../../_context/MachineContext';
import { trim } from '../../_utils/utils';

const arr = [];

function Machine() {
  const { assets = arr} = useContext(MachineContext);
  const { machine } = useParams();

  const asset = assets.find((item) => {
    return trim(item.name) === machine;
  });

  return ( 
    <div className="flex flex-col justify-center max-w-xs p-6 shadow-md rounded-xl sm:px-12 dark:bg-coolGray-900">
      { asset && (
        <>
          <img src={asset.image} alt="Profile face" className="w-32 h-32 mx-auto rounded-full" />
            <div className="space-y-4 text-center divide-y divide-coolGray-700">
              <div className="my-2 space-y-1">
                <h2 className="text-xl font-semibold sm:text-2xl">{asset.name}</h2>
                <p className="px-5 text-xs sm:text-base dark:text-coolGray-400">{asset.sensors}</p>
              </div>
              <span class="text-green-500 mr-8">Saude: {Math.floor(Number(asset.healthscore))}%</span>
            </div>
        </>
        )
      }
    </div>
  );
}

export default Machine;
