import React, { useState } from 'react';
import arrow from './arrow.svg';
import MachineItem from '../MachineItem/index.jsx';

const arr = [];
function MachineList({ status = arr, count = arr, assets = arr }){
  const [open, setOpen] = useState(false);

  function handleClick() {
    setOpen(!open);
  }

  return (
    <li>
      <div class="flex">
        <h3 onClick={handleClick} class="flex-1 flex-row ml-8 mt-8 mr-8 items-stretch ml-8 whitespace-nowrap inline-flex items-center justify-center px-4 py-2 border border-transparent rounded-md shadow-sm text-base font-medium text-white bg-indigo-600 hover:bg-indigo-700"> 
          {status}
          <span class="h-4 ml-3 text-green-500 font-semibold">{count}</span>
        </h3>
      </div>
      <ul>
        {
          open && (
            assets.map((item) => item.status === status ? <MachineItem asset={item}/> : '')
          )
        }
      </ul>
    </li>
  );
}

export default MachineList;
