import React, { useContext } from 'react';
import { useParams } from "react-router-dom";
import MachineContext from '../../_context/MachineContext';
import { trim } from '../../_utils/utils';

const arr = [];

function Machine() {
  const { assets = arr} = useContext(MachineContext);
  const { machine } = useParams();

  const asset = assets.find((item) => {
    return trim(item.name) === machine;
  });

  return ( 
    <div className="asset">
      { asset &&
        <>
          <span class="text-green-500 mr-8">{Math.floor(Number(asset.healthscore))}%</span>
          <span class="mr-8">{asset.name}</span>
          <span class="">{asset.sensors}</span>

          <div class="">
            <img src={asset.image} alt="Maquina" />
          </div>
        </>
      }
    </div>
  );
}

export default Machine;
